#include <stdio.h>
#include <openssl/bn.h>

void printBN(char *msg, BIGNUM *a)
{
    char *number_str_a = BN_bn2hex(a);
    printf("%s %s\n", msg, number_str_a);
    OPENSSL_free(number_str_a);
}

int main()
{
    // init
    BN_CTX *ctx = BN_CTX_new();

    BIGNUM *n = BN_new();
    BIGNUM *e = BN_new();
    // BIGNUM *d = BN_new();
    // Private key isn't used for verifying
    BIGNUM *M = BN_new();
    BIGNUM *C = BN_new();
    BIGNUM *S = BN_new();

    // assign values
    BN_hex2bn(&n, ""); //Modulus
    BN_dec2bn(&e, "65537"); //Exponent
    BN_hex2bn(&M, ""); //hash of the certificate body
    BN_hex2bn(&S, ""); //Signature

    // decrypt S: C = S^e mod n
    // If S=M^d mod n, then C=M
    BN_mod_exp(C, S, e, n, ctx);

    printBN("Hash:", M);
    printBN("Signature:", C);

    // clear sensitive data
    BN_clear_free(n);
    BN_clear_free(e);
    BN_clear_free(M);
    BN_clear_free(C);
    BN_clear_free(S);

    return 0;
}